        public void DeserializePlcType(Type t, object expected, byte[] value, bool isJson = false)
        {
            PlcDataMapperSerializer s = new();
            if (t == typeof(DateTime) && expected is string str)
            {
                expected = DateTime.ParseExact(str, "ddd dd MMM h:mm tt yyyy", CultureInfo.InvariantCulture);
            }

            object data = s.Deserialize(t, value);

            if (isJson && expected is string e)
            {
                string sdata = JsonSerializer.Serialize(data);
                Assert.Equal(e, sdata);
            }
            else
            {
                Assert.Equal(expected, data);
            }
        }


